import React from 'react'
import './index.css'
const menudata = [ {
  dish_name : 'Veg Cheese Twister',
  price : '59.5',
  description : 'A delight for veggie lovers!Delicious vegetarion Twister'
} ]
export const App = () => {

  return (
    <>
      <h2>Recommended Items</h2>
      {menudata.map((menu_item) =>{
        return <Item {...menu_item}></Item>;
      })}
    </>
  );
}
const Item = (props) =>
{
    const {dish_name , price ,description} = props;
    return(
      <article>
        <p className='dish_name'>{dish_name}</p>
        <span>{price}</span><br></br><br></br>
        <span>{description}</span>
      </article>
    );
}

export default App
