import React , { useState , useEffect} from 'react'
import './index.css'

const url = 'https://api.dotpe.in/api/catalog/store/1/menu?mDomain=fryerstory.in&saletype=delivery&serviceSubtype=delivery';

export const App = () => {
    const [data , setdata] = useState([]);
    const [menuitems , setmenuitems] = useState([]);
    const getdishes = async () =>
    {
      const response = await fetch(url);
      const data = await response.json();
      const menuitems = [data.menuItems];
      // const menuitems = [data.menuItems];
      // console.log(menuitems);

      // menuitems.map((object) =>{
      //   console.log(object)

      //   for(let key in object)
      //   {
      //     let subobject = object[key];
      //     console.log(subobject.itemName);
      //   }

        setdata(data);
        setmenuitems(menuitems);
      //});
    }

    useEffect(() => {
      getdishes();
    } , []);

    return(
      <>
          <h3>recommended items</h3>
          {menuitems.map((object) => {
            console.log(object);
            let req_info = [];
            for(let key in object)
            {
              let subobject = object[key];
              let temp_object = {
                name : subobject.itemName,
                description : subobject.description,
                veg : subobject.isVegetarian
              }
              req_info.push(temp_object);
            }
            return (
               <div>
                 {req_info.map((object) => {
                   return (
                   <div className = 'itemdetail'>
                   <ul>{object.veg}</ul>
                   <ul>{object.name}</ul>
                   <ul>{object.description}</ul>
                   </div>
                   );
                 })}
            </div>
             );
          })}
      </>
    );
  }

export default App
